insert into dbo.RecipeStep (Id, RecipeId, StepDescription, StepNumber) values (1, 1, 'Preheat oven to 425 degrees F (220 degrees C).', 1 );
insert into dbo.RecipeStep (Id, RecipeId, StepDescription, StepNumber) values (2, 1, 'Melt butter in a saucepan over medium-low heat', 2 );
insert into dbo.RecipeStep (Id, RecipeId, StepDescription, StepNumber) values (3, 1, 'Line a 9-inch pie dish with a crust.', 3 );
insert into dbo.RecipeStep (Id, RecipeId, StepDescription, StepNumber) values (4, 1, 'Bake in the preheated oven for 15 minutes. ', 4 );
insert into dbo.RecipeStep (Id, RecipeId, StepDescription, StepNumber) values (5, 2, 'Combine flour and baking powder in a large bowl. ', 1 );
insert into dbo.RecipeStep (Id, RecipeId, StepDescription, StepNumber) values (6, 2, 'Preheat the oven to 350 degrees F (175 degrees C). Grease a baking sheet with butter.', 2 );
insert into dbo.RecipeStep (Id, RecipeId, StepDescription, StepNumber) values (7, 2, 'Cut halved plums in half again, without cutting all the way through, so they fan out.', 3 );
insert into dbo.RecipeStep (Id, RecipeId, StepDescription, StepNumber) values (8, 2, 'Roll pastry dough out on a lightly floured work surface. Line the bottom and sides of the prepared baking sheet with dough. Mix 2 tablespoons sugar and cinnamon together in a bowl.', 4 );
insert into dbo.RecipeStep (Id, RecipeId, StepDescription, StepNumber) values (9, 3, 'Preheat oven to 375 degrees F (190 degrees C). Sift the confectioners sugar and cake flour together and set aside.', 1 );
insert into dbo.RecipeStep (Id, RecipeId, StepDescription, StepNumber) values (10, 3, 'In a large bowl, whip the egg whites on high speed until foamy.', 2 );
insert into dbo.RecipeStep (Id, RecipeId, StepDescription, StepNumber) values (11, 3, 'Pour batter into a 10-inch tube pan. Sprinkle top with chopped almonds. ',3 );
